function pega_segunda(){
    $data = date("Y-m-d");
    $diasemana_numero =date("w", strtotime($data));
    $numSemana = $diasemana_numero -1 ;    
     $dataA = date("Y-m-d", strtotime("-$numSemana days",strtotime($data)));
    
    return($dataA); 
}
function acha_dias($seguunda,$diaAcahar){
    for ($d = 0; $d < 6; $d++) {
     $dataAcada = date("Y-m-d", strtotime("+$d days",strtotime($seguunda)));       
    $diasemana_numero =date("w", strtotime($dataAcada));      
    if($diasemana_numero == $diaAcahar){
       
        return($dataAcada);
    }
    }    
}
function dias_de_aula($datainciao,$semanhas){
        $dataTmp = $datainciao;      
        for ($d = 0; $d < $semanhas; $d++) {            
            $data = date("Y-m-d", strtotime("+7 days",strtotime($dataTmp))); 
            $datas[$d]=$data;
            $dataTmp = $data;
            }
        return($datas);
        }   
    
